import { Environment, Lightformer, OrbitControls, PerspectiveCamera } from "@react-three/drei";

const Stage = (props) => {
  return(
    <>
      <ambientLight intensity={1.2}/>
      <Environment frames={Infinity} resolution={256}>
        {/* Ceiling */}
        <Lightformer intensity={10} rotation-x={Math.PI / 2} position={[0, 5, -5]} scale={[10, 10, 1]} />
        {/* Sides */}
        <Lightformer intensity={40} rotation-y={Math.PI / 2} position={[-2, 1, -2]} scale={[20, 0.1, 1]} />
        <Lightformer rotation-y={Math.PI / 2} position={[-5, -1, -1]} scale={[20, 0.5, 1]} />
        <Lightformer rotation-y={-Math.PI / 2} position={[10, 1, 0]} scale={[20, 1, 1]} />
        <Lightformer intensity={40} rotation-y={Math.PI} position={[0, 1, -2]} scale={[20, 0.1, 1]} />
      </Environment>

      <PerspectiveCamera
        makeDefault
        position={props.position}
        rotation={props.rotation}
      />
    </>
  )
}

export default Stage;
