import Stage from './Stage.js';
import Car from './Car.js';
import BackGroundBehind from './BackGroundBehind.js';
import BackGroundFront from './BackGroundFront.js';
import { useState } from 'react';

const Right = (props) => {
  const [slideNum, setSlideNum] = useState(4);

  let stagePosition = 
    slideNum == 1 ? [-1.5, 0, 5.5] 
    : slideNum == 2 ? [0, 0, 5.5] 
    : slideNum == 3 ? [-0.8, -0.3, 3] 
    : slideNum == 4 ? [0.8, -0.2, 3] 
    : undefined
  let stageRotation = 
    slideNum == 1 ? [0, 0, 0] 
    : slideNum == 2 ? [0, 0, 0] 
    : slideNum == 3 ? [0, 0, 0] 
    : slideNum == 4 ? [0, 0, 0] 
    : undefined
  let carPosition = 
    slideNum == 1 ? [0, -1, 0] 
    : slideNum == 2 ? [0, -1, 0] 
    : slideNum == 3 ? [0, -1, 0] 
    : slideNum == 4 ? [0, -1, 0] 
    : undefined
  let carRotation = 
    slideNum == 1 ? [0, -1.5, 0] 
    : slideNum == 2 ? [0.1, -0.7, 0] 
    : slideNum == 3 ? [-0.05, -0.1, 0] 
    : slideNum == 4 ? [-0.1, -3, 0] 
    : undefined

  return(
    <>
      <Stage position={stagePosition} rotation={stageRotation}/>
      <Car position={carPosition} rotation={carRotation}/>
      <BackGroundBehind/>
      <BackGroundFront num={1}/>
    </>
  );
}

export default Right;
